'use strict';

module.exports = {
	convertNumberToCpfCnpj: function(rawValue) {
    if( !rawValue.replace ){
      if( rawValue === 0 ){
        rawValue = '';
      }
      else{
        rawValue = rawValue.toString();
        while( rawValue.length < 11 || rawValue.length > 11 && rawValue.length < 14 ){
          rawValue = '0' + rawValue;
        }
      }
    }
    return rawValue;
	}
};
